import{_ as s,o as r,c as o,a}from"./app.192b32a7.js";const i="/developer-toolbox/recurrence/knowledge/maslowPyramid.jpeg",n="/developer-toolbox/assets/dikw-pyramid.e627fe34.png",e="/developer-toolbox/assets/set.fb2e8bab.png",t="/developer-toolbox/assets/recursiveSet.02034807.png",l="/developer-toolbox/assets/you.e99b394f.png",d="/developer-toolbox/assets/woman.37175c11.png",h="/developer-toolbox/assets/setClass.a8901f5e.png",c="/developer-toolbox/assets/abstractRecursiveSet.b7b24fbd.png",g="/developer-toolbox/assets/partOfWhole.4a99d7b7.png",m="/developer-toolbox/assets/classification.cb9ba7fe.png",p="/developer-toolbox/assets/aggregation.3be4c67b.png",u="/developer-toolbox/assets/multipleClassification.f1f00c47.png",f="/developer-toolbox/assets/orderFromSet.2f4ccc2c.png",b="/developer-toolbox/assets/sequence.8f745628.png",w="/developer-toolbox/assets/tree.64ae06db.png",v="/developer-toolbox/assets/graph.b5d1c710.png",y="/developer-toolbox/assets/hierarchy.02e20055.png",q=JSON.parse('{"title":"Knowledge","description":"","frontmatter":{},"headers":[{"level":2,"title":"Why? \u{1F914}","slug":"why-\u{1F914}","link":"#why-\u{1F914}","children":[]},{"level":2,"title":"What? \u{1F913}","slug":"what-\u{1F913}","link":"#what-\u{1F913}","children":[]},{"level":2,"title":"For what? \u{1F632}","slug":"for-what-\u{1F632}","link":"#for-what-\u{1F632}","children":[]},{"level":2,"title":"How? \u{1F9D0}","slug":"how-\u{1F9D0}","link":"#how-\u{1F9D0}","children":[{"level":3,"title":"Sets","slug":"sets","link":"#sets","children":[]},{"level":3,"title":"Classification strategies","slug":"classification-strategies","link":"#classification-strategies","children":[]}]}],"relativePath":"learn/recurrence/knowledge/index.md","lastUpdated":1665993872000}'),_={name:"learn/recurrence/knowledge/index.md"},x=a('<h1 id="knowledge" tabindex="-1">Knowledge <a class="header-anchor" href="#knowledge" aria-hidden="true">#</a></h1><h2 id="why-\u{1F914}" tabindex="-1">Why? \u{1F914} <a class="header-anchor" href="#why-\u{1F914}" aria-hidden="true">#</a></h2><p>Why do we want to study and understand knowledge?</p><p>Because we can only observe the world from our perspective, our shoes. Knowledge is what allowed our ancestors to survive and eat the things that would not kill us.</p><p><strong>Maslow pyramid</strong></p><p><img src="'+i+'" alt="Maslow pyramid"></p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Nowadays in order to survive we need a job and thus (as software engineers) <strong>knowledge of our craft</strong> is what will allow us to cover our physiological needs and <strong>survive</strong>.</p></div><h2 id="what-\u{1F913}" tabindex="-1">What? \u{1F913} <a class="header-anchor" href="#what-\u{1F913}" aria-hidden="true">#</a></h2><p><strong>DIKW Pyramid</strong></p><p><img src="'+n+'" alt="DIKW pyramid"></p><table><thead><tr><th>Level</th><th>Definition</th><th>Examples</th></tr></thead><tbody><tr><td><strong>Noise</strong></td><td>Physic signal (visual, audible...) not standardized, without pattern.</td><td><em>\xF1laksjdfk, xxi, k34hsksi29sk2, ]})\xBF:?({#!!, :-*, \u2026\u200B, kid clapping, hit the piano keys...</em></td></tr><tr><td><strong>Data</strong></td><td>Magnitude of some concrete qualitative or quantitative characteristic, without context or meaning, with a precise. <strong>format</strong></td><td><em>1850, 7009.7 thousands of millions, 9,6%, (, ., !, 8-o,...</em></td></tr><tr><td><strong>Information</strong></td><td>Application of <strong>structured order</strong> over data with the goal of giving them meaning. Information is organized data, related element inside a system structure, describes in a concise <strong>language</strong></td><td><em>1850 son las retrasmisiones en directo de las campanadas de fin de a\xF1o que emocionaron al 9,6% de los 7009.7 miles de millones de personas que lo celebramos en 2014 a la vez, decimocuarto aniversario anual dentro del siglo xxi.</em></td></tr><tr><td><strong>Knowledge</strong></td><td><strong>Comprehend</strong> a specific subject, trough experience or education. It builds on top of information to provide context. The key difference between knowledge and information is that knowledge gives us power to make decisions.</td><td><em>You know how to hang out parties you usually attend</em></td></tr><tr><td><strong>Wisdom</strong></td><td>Is the <strong>optimal judgement</strong>, which reflects a deep understanding of people, things, events or situations.</td><td><em>Knows how to enjoy being with others in any party</em></td></tr></tbody></table><h2 id="for-what-\u{1F632}" tabindex="-1">For what? \u{1F632} <a class="header-anchor" href="#for-what-\u{1F632}" aria-hidden="true">#</a></h2><p>To obtain <strong>effective</strong> and <strong>efficient solutions</strong> from <strong>complex systems</strong> in any <strong>productive sector</strong>.</p><p>In order to exploit all resources in the universe we need to study, investigate, analyze, synthesize and know them.</p><h2 id="how-\u{1F9D0}" tabindex="-1">How? \u{1F9D0} <a class="header-anchor" href="#how-\u{1F9D0}" aria-hidden="true">#</a></h2><h3 id="sets" tabindex="-1">Sets <a class="header-anchor" href="#sets" aria-hidden="true">#</a></h3><table><thead><tr><th>Definition</th><th>Concrete</th></tr></thead><tbody><tr><td>A set is an <strong>unordered collection</strong> of <strong>not repeated elements</strong>, <strong>associated by a pattern</strong></td><td><img src="'+e+'" alt="set"></td></tr><tr><td>A <strong>recursive set</strong> is one <strong>whose elements can also be other sets</strong>.</td><td><img src="'+t+'" alt="Recursive set"></td></tr></tbody></table><h4 id="objects-and-classes" tabindex="-1">Objects and Classes <a class="header-anchor" href="#objects-and-classes" aria-hidden="true">#</a></h4><table><thead><tr><th>Objects</th><th>Classes</th></tr></thead><tbody><tr><td>An <strong>object</strong> is a set of elements which are other <strong>objects</strong> which at the same time... <strong>recursively</strong>!</td><td>A <strong>class</strong> is a set whose elements are the <strong>common characteristics of another set of similar objects</strong></td></tr><tr><td><em>That peach! The peach I ate; the third peach in the basket, a bit red, that one!</em></td><td><em>Prunus persica, the peach is a deciduous tree first domesticated and cultivated in Zhejiang province of Eastern China...</em></td></tr><tr><td>@&gt;\u2192---</td><td><em>An emoticon short for &quot;emotion icon&quot;, also known simply as an emote, is a pictorial representation of a facial expression using characters\u2014usually punctuation marks, numbers, and letters\u2014to express a person&#39;s feelings, mood or reaction, or as a time-saving method</em></td></tr><tr><td>A real or virtual being with concrete values in its characteristics</td><td><em>A book, a desription, a definition of its common characteristics, without concrete</em> values</td></tr><tr><td><img src="'+l+'" alt="you"></td><td><img src="'+d+'" alt="woman"></td></tr><tr><td><strong>Focus</strong> on a <strong>set of objects</strong> that <strong>collaborate a lot</strong>, with <strong>high cohesion</strong> and <strong>low coupling</strong>, regardless of their similarity.<br><br><em>A human body is a set of head, trunk, four extremities --&gt; no similarity</em><br><br><em>A swarm is a group of bees --&gt; similar</em></td><td><strong>Focus</strong> on a <strong>set of common characteristics of a set of objects that are similar</strong>, regardless of whether they <strong>collaborate a lot or not</strong>.<br><br><em>The Human, they collaborate between them to the point they are kings of the world even though there are other superior species in strength, speed...</em><br><br><em>The service of gas stations, which do not collaborate between them</em></td></tr><tr><td><img src="'+e+'" alt="set"></td><td><img src="'+t+'" alt="Recursive set"></td></tr><tr><td><img src="'+h+'" alt="Set class"></td><td><img src="'+c+'" alt="Abstract recursive set"></td></tr></tbody></table><h4 id="abstract-classes" tabindex="-1">Abstract classes <a class="header-anchor" href="#abstract-classes" aria-hidden="true">#</a></h4><p>An abstract class is the intersection set of common characteristics from a set of similar classes.</p><ul><li>Living being <ul><li>Plants</li><li>Fungi</li><li>Animals <ul><li>Invertebrates</li><li>Vertebrates <ul><li>Fishes</li><li>Mammals <ul><li>Canines</li><li>Felines</li><li>Primates <ul><li>Monkeys</li><li>Humans</li></ul></li></ul></li></ul></li></ul></li></ul></li></ul><h4 id="composition-vs-classification-hierarchies" tabindex="-1">Composition vs Classification Hierarchies <a class="header-anchor" href="#composition-vs-classification-hierarchies" aria-hidden="true">#</a></h4><table><thead><tr><th>Composition Hierarchy</th><th>Classification Hierarchy</th></tr></thead><tbody><tr><td>Emerges from the <strong>union of the object parts of a whole</strong> that is as well part of a whole that is as well...</td><td>Emerges from the <strong>intersection of the characteristics of similar classes</strong> that at the same time descends from another base class that at the same time...</td></tr><tr><td><img src="'+g+'" alt="Composition hierarchy"></td><td><img src="'+m+'" alt="Classification hierarchy"></td></tr><tr><td>A part is an element of the whole and is the whole for other parts</td><td>A derived class is an specialization of the base class and is the base of other specializations</td></tr><tr><td>Example: You are the set of organs that each one is a set of tissue which is a set of cells...</td><td>Example: People is the union of the set of men and women with their general, common and shared characteristics</td></tr></tbody></table><h4 id="aggregation-vs-multiple-classification-hierarchies" tabindex="-1">Aggregation vs Multiple Classification Hierarchies <a class="header-anchor" href="#aggregation-vs-multiple-classification-hierarchies" aria-hidden="true">#</a></h4><table><thead><tr><th>Aggregation Hierarchy</th><th>Multiple Classification Hierarchy</th></tr></thead><tbody><tr><td>A part object belongs to many whole objects</td><td>A derived class shares characteristics with many base classes</td></tr><tr><td><img src="'+p+'" alt="Aggregation hierarchy"></td><td><img src="'+u+'" alt="Multiple Classification hierarchy"></td></tr><tr><td>I belong to the family I was raised in and the one I created when I grew</td><td>My cousin is Spanish and French, double nationality</td></tr><tr><td></td><td></td></tr></tbody></table><h4 id="association-relationship" tabindex="-1">Association relationship <a class="header-anchor" href="#association-relationship" aria-hidden="true">#</a></h4><p>Sets do not have order but can be created from itself, order is an <strong>emergent characteristic</strong>!</p><ul><li>From nothing to quantic matter</li><li>From matter to life in DNA, self-replicable</li><li>From noise to data by collaboration</li><li>From data to information by association</li><li>From life to intelligence by neural networks</li><li>From intelligence to conscience by the free will?</li></ul><p>Ordered Pair (a,b), a --&gt; b, ...</p><p><img src="'+f+'" alt="Order from set"></p><h4 id="objects-structures" tabindex="-1">Objects structures <a class="header-anchor" href="#objects-structures" aria-hidden="true">#</a></h4><h5 id="sequence" tabindex="-1">Sequence <a class="header-anchor" href="#sequence" aria-hidden="true">#</a></h5><p>Conditions:</p><ul><li>0..1 previous elements (0 for the <strong>first</strong> element)</li><li>0..1 next elements (0 for the <strong>last</strong> element)</li></ul><p><img src="'+b+'" alt="Sequence"></p><hr><h5 id="tree" tabindex="-1">Tree <a class="header-anchor" href="#tree" aria-hidden="true">#</a></h5><p>Conditions:</p><ul><li>0..1 previous elements (0 for the <strong>root</strong> element)</li><li>0..N next elements (0 for the <strong>last</strong> elements)</li></ul><p><img src="'+w+'" alt="Tree"></p><hr><h5 id="graph" tabindex="-1">Graph <a class="header-anchor" href="#graph" aria-hidden="true">#</a></h5><p>Conditions:</p><ul><li>0..N previous elements (0 or N for any element)</li><li>0..N next elements (0 or N for any element)</li></ul><p><img src="'+v+'" alt="Graph"></p><hr><h5 id="hierarchy" tabindex="-1">Hierarchy <a class="header-anchor" href="#hierarchy" aria-hidden="true">#</a></h5><p>Conditions:</p><ul><li>0..N previous elements (0 or N for any element)</li><li>0..N next elements (0 or N for any element)</li><li><strong>Without cycles</strong></li></ul><p><img src="'+y+'" alt="Hierarchy"></p><h3 id="classification-strategies" tabindex="-1">Classification strategies <a class="header-anchor" href="#classification-strategies" aria-hidden="true">#</a></h3>',52),k=[x];function A(C,j,I,F,N,S){return r(),o("div",null,k)}const H=s(_,[["render",A]]);export{q as __pageData,H as default};
